  var res = String(result).replace(/\[|\]/g,'')
  if(res.match(/\|/)){
   	var re = res.split("|")
	   re.shift()
	   return re
   }
  else if(res.match(/,/)){
     return	res.split(',')	   
 	}
}
kind()

